<?phpclass ShoppingCard {}// Cвойства класса - переменные, которые содержат класс// Длясоздания объекта класса используем new// Затем пишем имя класса ShoppingCard// Экземпляр класса - переменная $product, может хранить свойства и методы класса$product1 = new ShoppingCard();$product2 = new ShoppingCard();$product3 = new ShoppingCard();// Каждый обьект имеет свой индитификаторvar_dump($product1 instanceof ShoppingCard);var_dump($product2 instanceof Card);var_dump($product3);// Оператор instanceof для проверки: является ли объект экземпляром какого-то классаphpinfo();?>